var getUrlVars=function(){for(var e,t=[],r=window.location.href.slice(window.location.href.indexOf("?")+1).split("&"),l=0;l<r.length;l++)e=r[l].split("="),t.push(e[0]),t[e[0]]=e[1];return t},urlVars=getUrlVars();if("undefined"!=typeof urlVars.title)var coverTitle=atob(urlVars.title);else var coverTitle="Title was not passed";if("undefined"!=typeof urlVars.subtitle)var coverSubtitle=atob(urlVars.subtitle);else var coverSubtitle="Subtitle was not passed";document.querySelector("h1").innerText=coverTitle,document.querySelector("h2").innerText=coverSubtitle;